import styled from "styled-components";


const subColor = "grey";
const mainColor = "black";
const shrinkLabel = `top: -14px;font-size: 12px;color:${mainColor};`;

const FormInputStyled = styled.div`
  position: relative;
  margin: 45px 0;

  .form-input {
    background: white none;
    color: ${subColor};
    font-size: 18px;
    padding: 10px 10px 10px 5px;
    display: block;
    width: 100%;
    border: none;
    border-radius: 0;
    border-bottom: 1px solid $sub-color;
    margin: 25px 0;

    &:focus {
      outline: none;
    }
  }
  &:focus ~ .form-input-label {
    ${shrinkLabel}
  }

  input[type="password"] {
    letter-spacing: 0.3em;
  }

  .form-input-label {
    color: ${subColor};
    font-size: 16px;
    font-weight: normal;
    position: absolute;
    pointer-events: none;
    left: 5px;
    top: 10px;
    transition: 300ms ease all;

    &.shrink {
     ${shrinkLabel}
    }
  }
`;
export default FormInputStyled;
